jest.unmock('../api-errors');
describe('node_modules/infra/api-errors.js', function(){

    it('should be able to create error response from template', function() {
        var apiErrors = require('../api-errors');
        var ErrorRes = require('../error-res');

        var errRes = apiErrors.INTERNAL_SERVER_ERROR.new();
        expect(errRes instanceof ErrorRes).toBeTruthy();
        expect(errRes.error_name).toEqual('INTERNAL_SERVER_ERROR');
        expect(errRes.status_code).toEqual(500);
        expect(errRes.hasError()).toBeFalsy();
    });
} );
