'use strict'
var SNError = require('./sn-error');

/**
 *
 * @param message: string - created by developer when throwing the error.
 * @param query: string - sql query
 * @param values: [
 *      value: any primitive - to replace its placeholder in query
 * ]
 */
class ScrappingError extends SNError{

    constructor(message, url) {
        super(message);
        this.url = url;
    }
};

module.exports = ScrappingError;