module.exports = {
    filterData : function (data, filteringFields){
        var result = {};

        for(var i=0; i < filteringFields.length; i++) {
            result[filteringFields[i]] = data[filteringFields[i]];
        }

        return result;
    },

    /**
     * Convert array to map where map key = "id" fields of array's object
     *
     * NOTE - Use Array.prototype.reduce is supported in IE9+
     *
     * @param objects
     * @returns {{}}
     */
    convertArrayToMap: function (objects){
    	var mapObj = {};
    	for(var i=0; i < objects.length; i++){
    		mapObj[objects[i].id] = objects[i];
    	}

    	return mapObj;
    }
}